import { MsgBurnToken } from "./types/token/tx";
import { MsgIssueToken } from "./types/token/tx";
import { MsgTransferTokenOwner } from "./types/token/tx";
const types = [
    ["/alice.checkers.token.MsgEditToken", MsgEditToken],
    ["/alice.checkers.token.MsgMintToken", MsgMintToken],
    ["/alice.checkers.token.MsgBurnToken", MsgBurnToken],
    ["/alice.checkers.token.MsgIssueToken", MsgIssueToken],
    ["/alice.checkers.token.MsgTransferTokenOwner", MsgTransferTokenOwner],
];
export const MissingWalletError = new Error("wallet is required");
export const registry = new Registry(types);
const defaultFee = {
    amount: [],
    gas: "200000",
};
const txClient = async (wallet, { addr: addr } = { addr: "http://localhost:26657" }) => {
    if (!wallet)
        throw MissingWalletError;
    let client;
    if (addr) {
        client = await SigningStargateClient.connectWithSigner(addr, wallet, { registry });
    }
    else {
        client = await SigningStargateClient.offline(wallet, { registry });
    }
    const { address } = (await wallet.getAccounts())[0];
    return {
        signAndBroadcast: (msgs, { fee, memo } = { fee: defaultFee, memo: "" }) => client.signAndBroadcast(address, msgs, fee, memo),
        msgEditToken: (data) => ({ typeUrl: "/alice.checkers.token.MsgEditToken", value: MsgEditToken.fromPartial(data) }),
        msgMintToken: (data) => ({ typeUrl: "/alice.checkers.token.MsgMintToken", value: MsgMintToken.fromPartial(data) }),
        msgBurnToken: (data) => ({ typeUrl: "/alice.checkers.token.MsgBurnToken", value: MsgBurnToken.fromPartial(data) }),
        msgIssueToken: (data) => ({ typeUrl: "/alice.checkers.token.MsgIssueToken", value: MsgIssueToken.fromPartial(data) }),
        msgTransferTokenOwner: (data) => ({ typeUrl: "/alice.checkers.token.MsgTransferTokenOwner", value: MsgTransferTokenOwner.fromPartial(data) }),
    };
};
const queryClient = async ({ addr: addr } = { addr: "http://localhost:1317" }) => {
    return new Api({ baseUrl: addr });
};
