import { SigningStargateClient } from "@cosmjs/stargate";
import { Registry } from "@cosmjs/proto-signing";
import { Api } from "./rest";
import { MsgMintnft } from "./types/nft/tx";
import { MsgTransferDenom } from "./types/nft/tx";
import { MsgTransfernft } from "./types/nft/tx";
import { MsgBurnnft } from "./types/nft/tx";
import { MsgIssueDenom } from "./types/nft/tx";
import { MsgEditnft } from "./types/nft/tx";
const types = [
    ["/irismod.nft.MsgMintnft", MsgMintnft],
    ["/irismod.nft.MsgTransferDenom", MsgTransferDenom],
    ["/irismod.nft.MsgTransfernft", MsgTransfernft],
    ["/irismod.nft.MsgBurnnft", MsgBurnnft],
    ["/irismod.nft.MsgIssueDenom", MsgIssueDenom],
    ["/irismod.nft.MsgEditnft", MsgEditnft],
];
export const MissingWalletError = new Error("wallet is required");
export const registry = new Registry(types);
const defaultFee = {
    amount: [],
    gas: "200000",
};
const txClient = async (wallet, { addr: addr } = { addr: "http://localhost:26657" }) => {
    if (!wallet)
        throw MissingWalletError;
    let client;
    if (addr) {
        client = await SigningStargateClient.connectWithSigner(addr, wallet, { registry });
    }
    else {
        client = await SigningStargateClient.offline(wallet, { registry });
    }
    const { address } = (await wallet.getAccounts())[0];
    return {
        signAndBroadcast: (msgs, { fee, memo } = { fee: defaultFee, memo: "" }) => client.signAndBroadcast(address, msgs, fee, memo),
        msgMintnft: (data) => ({ typeUrl: "/irismod.nft.MsgMintnft", value: MsgMintnft.fromPartial(data) }),
        msgTransferDenom: (data) => ({ typeUrl: "/irismod.nft.MsgTransferDenom", value: MsgTransferDenom.fromPartial(data) }),
        msgTransfernft: (data) => ({ typeUrl: "/irismod.nft.MsgTransfernft", value: MsgTransfernft.fromPartial(data) }),
        msgBurnnft: (data) => ({ typeUrl: "/irismod.nft.MsgBurnnft", value: MsgBurnnft.fromPartial(data) }),
        msgIssueDenom: (data) => ({ typeUrl: "/irismod.nft.MsgIssueDenom", value: MsgIssueDenom.fromPartial(data) }),
        msgEditnft: (data) => ({ typeUrl: "/irismod.nft.MsgEditnft", value: MsgEditnft.fromPartial(data) }),
    };
};
const queryClient = async ({ addr: addr } = { addr: "http://localhost:1317" }) => {
    return new Api({ baseUrl: addr });
};
export { txClient, queryClient, };
