import { createI18n } from "vue-i18n";

const i18n = createI18n({
  //设置默认地区
  locale: localStorage.getItem("lang") || "zh",
  //备选区域
  fallbackLocale: "en",
  //设置地区对应的语言
  messages: {
    ch: require("./zh.js"),
    en: require("./en.js"),
  },
});

export default i18n;
