import { SigningStargateClient } from "@cosmjs/stargate";
import { Registry } from "@cosmjs/proto-signing";
import { Api } from "./rest";
import { MsgRemoveLiquidity } from "./types/coinswap/tx";
import { MsgAddLiquidity } from "./types/coinswap/tx";
import { MsgSwapOrder } from "./types/coinswap/tx";
const types = [
    ["/irismod.coinswap.MsgRemoveLiquidity", MsgRemoveLiquidity],
    ["/irismod.coinswap.MsgAddLiquidity", MsgAddLiquidity],
    ["/irismod.coinswap.MsgSwapOrder", MsgSwapOrder],
];
export const MissingWalletError = new Error("wallet is required");
export const registry = new Registry(types);
const defaultFee = {
    amount: [],
    gas: "200000",
};
const txClient = async (wallet, { addr: addr } = { addr: "http://localhost:26657" }) => {
    if (!wallet)
        throw MissingWalletError;
    let client;
    if (addr) {
        client = await SigningStargateClient.connectWithSigner(addr, wallet, { registry });
    }
    else {
        client = await SigningStargateClient.offline(wallet, { registry });
    }
    const { address } = (await wallet.getAccounts())[0];
    return {
        signAndBroadcast: (msgs, { fee, memo } = { fee: defaultFee, memo: "" }) => client.signAndBroadcast(address, msgs, fee, memo),
        msgRemoveLiquidity: (data) => ({ typeUrl: "/irismod.coinswap.MsgRemoveLiquidity", value: MsgRemoveLiquidity.fromPartial(data) }),
        msgAddLiquidity: (data) => ({ typeUrl: "/irismod.coinswap.MsgAddLiquidity", value: MsgAddLiquidity.fromPartial(data) }),
        msgSwapOrder: (data) => ({ typeUrl: "/irismod.coinswap.MsgSwapOrder", value: MsgSwapOrder.fromPartial(data) }),
    };
};
const queryClient = async ({ addr: addr } = { addr: "http://localhost:1317" }) => {
    return new Api({ baseUrl: addr });
};
